import React from 'react';
import './contacto.css';
import { useForm } from "react-hook-form";
import instance from "../../../Utils/axiosInstance";

export default function Contact() {

const { register, handleSubmit, formState: { errors }, reset} = useForm();

const onSubmit = async (data) => {
  try {
    const params = new URLSearchParams();
    Object.entries(data).map(pair => {
      return params.append(pair[0], pair[1]);
    })
    reset();
    await instance.post("contact", params);
  } catch (err) {
    console.log(err);
  }
}

return ( 
    <form onSubmit={handleSubmit(onSubmit)}  className="mb-2">

        <div class="form-group row mb-4">
        <label id="label_contacto" className="col-m-3">Nombre</label>
        <div className="col-m-9">
        <label id="label_contacto"for="name" class="col-sm-3 col-form-label text-right">Nombre y apellido</label>
        <div class="col-sm-6">
        <input name="Name" className="form-control"
              {...register("Name", {
                required: true,
                maxLength: 20,
                pattern: /^[A-Za-z]+$/i
              })} />
        </div>
        <div className="text-danger">
          {errors?.Name?.type === "required" && <p>Este campo es requerido</p>}
          {errors?.Name?.type === "maxLength" && (<p>El nombre no puede tener más de 20 caracteres</p>)}
          {errors?.Name?.type === "pattern" && (<p>Solo se permiten caracteres alfabéticos</p>)}</div>
        </div>
        </div>

        <div class="form-group row mb-3">
        <div className="col-6">
          <label id="label_contacto" className="col-m-3">Email</label>
          <div className="col-m-9">
            <input
              className="form-control"
              name="email"
              type="email"
              {...register("email", {
                required: true,
                pattern: new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i),
              })} />
          </div>
          <div className="text-danger">
            {errors?.email?.type === "required" && <p>Este campo es requerido</p>}
            {errors?.email?.type === "pattern" && (<p>Ingrese un email válido</p>)}</div>
        </div>
        </div>

        <button type="submit" value="Submit" className="btn btn-outline-dark btn-lg">Enviar</button>
    </form>
    ) 
  }
